// Syst√®me d'√©v√©nements personnalis√©
const EventBus = {
  listeners: {},

  on(event, callback) {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }
    this.listeners[event].push(callback);
    console.log(`üì° Listener ajout√© pour: ${event}`);
  },

  emit(event, data) {
    if (this.listeners[event]) {
      this.listeners[event].forEach(callback => callback(data));
      console.log(`üîî √âv√©nement √©mis: ${event}`);
    }
  },

  off(event, callback) {
    if (this.listeners[event]) {
      this.listeners[event] = this.listeners[event]
        .filter(cb => cb !== callback);
      console.log(`üîá Listener retir√© pour: ${event}`);
    }
  }
};

// Utilisation
EventBus.on('userLogin', (user) => {
  console.log(`‚úÖ Bienvenue ${user.name} !`);
});

EventBus.on('userLogin', (user) => {
  console.log(`üìä Chargement du profil...`);
});

