const CounterApp = {
  count: 0,
  events: {},

  on(event, callback) {
    if (!this.events[event]) this.events[event] = [];
    this.events[event].push(callback);
  },

  emit(event) {
    if (this.events[event]) {
      this.events[event].forEach(cb => cb());
    }
  },

  increment() {
    this.count++;
    this.emit('change');
  },

  decrement() {
    this.count--;
    this.emit('change');
  },

  reset() {
    this.count = 0;
    this.emit('reset');
    this.emit('change');
  }
};

CounterApp.on('change', () => console.log('Count:', CounterApp.count));
CounterApp.on('reset', () => console.log('üîÑ Reset !'));
CounterApp.increment();
CounterApp.increment();
CounterApp.decrement();
CounterApp.reset();

