const TodoApp = {
  // √âtat de l'application
  state: {
    todos: [],
    nextId: 1
  },

  // Manipulation DOM
  DOM: {
    render() {
      console.log('üìã Rendu de', TodoApp.state.todos.length, 't√¢ches');
      // TODO: Afficher les t√¢ches
    },

    updateCount() {
      const total = TodoApp.state.todos.length;
      const completed = TodoApp.state.todos.filter(t => t.done).length;
      console.log(`üìä ${completed}/${total} compl√©t√©es`);
    }
  },

  // Logique m√©tier
  addTodo(text) {
    const todo = {
      id: this.state.nextId++,
      text,
      done: false,
      createdAt: new Date()
    };
    this.state.todos.push(todo);
    this.DOM.render();
    this.DOM.updateCount();
  },

  toggleTodo(id) {
    const todo = this.state.todos.find(t => t.id === id);
    if (todo) {
      todo.done = !todo.done;
      this.DOM.render();
      this.DOM.updateCount();
    }
  },

  deleteTodo(id) {
    this.state.todos = this.state.todos.filter(t => t.id !== id);
    this.DOM.render();
    this.DOM.updateCount();
  },

  // Initialisation
  init() {
    console.log('üöÄ TodoApp initialis√©e');
    this.DOM.render();
    this.DOM.updateCount();
  }
};

// Test
TodoApp.init();
TodoApp.addTodo('Apprendre le JavaScript');
TodoApp.addTodo('Faire le TD');
TodoApp.toggleTodo(1);
TodoApp.DOM.updateCount();

