// Simulation du syst√®me de grille Bootstrap
const Grid = {
  breakpoints: {
    xs: 0,
    sm: 576,
    md: 768,
    lg: 992,
    xl: 1200,
    xxl: 1400
  },

  getCurrentBreakpoint(width) {
    if (width >= this.breakpoints.xxl) return 'xxl';
    if (width >= this.breakpoints.xl) return 'xl';
    if (width >= this.breakpoints.lg) return 'lg';
    if (width >= this.breakpoints.md) return 'md';
    if (width >= this.breakpoints.sm) return 'sm';
    return 'xs';
  },

  calculateColumns(className, screenWidth) {
    // Exemple: 'col-md-6 col-lg-4'
    const classes = className.split(' ');
    const breakpoint = this.getCurrentBreakpoint(screenWidth);

    // Trouver la classe correspondante
    const relevantClass = classes
      .reverse()
      .find(c => {
        const match = c.match(/col-(xs|sm|md|lg|xl|xxl)-(\d+)/);
        if (!match) return false;
        const bp = match[1];
        return this.breakpoints[bp] <= screenWidth;
      });

    if (relevantClass) {
      const cols = relevantClass.match(/-(\d+)$/)[1];
      return parseInt(cols);
    }
    return 12; // Par d√©faut
  }
};

// Tests
console.log('=== Syst√®me de Grille Bootstrap ===');
console.log('Mobile (400px):', Grid.getCurrentBreakpoint(400));
console.log('Tablette (800px):', Grid.getCurrentBreakpoint(800));
console.log('Desktop (1300px):', Grid.getCurrentBreakpoint(1300));

const colClass = 'col-12 col-md-6 col-lg-4';
console.log('\nColonnes utilis√©es:');
console.log('  Mobile:', Grid.calculateColumns(colClass, 400), '/12');
console.log('  Tablette:', Grid.calculateColumns(colClass, 800), '/12');
console.log('  Desktop:', Grid.calculateColumns(colClass, 1300), '/12');

