// Store avec pattern Observer
class Store {
  constructor(initialState = {}) {
    this.state = initialState;
    this.listeners = [];
  }

  // R√©cup√©rer l'√©tat
  getState() {
    return { ...this.state };
  }

  // Mettre √† jour l'√©tat
  setState(updates) {
    this.state = { ...this.state, ...updates };
    this.notify();
  }

  // S'abonner aux changements
  subscribe(listener) {
    this.listeners.push(listener);
    console.log(`üì° Listener ajout√© (total: ${this.listeners.length})`);

    // Retourner fonction de d√©sabonnement
    return () => {
      this.listeners = this.listeners.filter(l => l !== listener);
      console.log(`üîá Listener retir√© (total: ${this.listeners.length})`);
    };
  }

  // Notifier tous les listeners
  notify() {
    console.log(`üîî √âtat mis √† jour, notification de ${this.listeners.length} listeners`);
    this.listeners.forEach(listener => listener(this.state));
  }
}

// Actions
const actions = {
  increment: (store) => {
    const { count } = store.getState();
    store.setState({ count: count + 1 });
  },

  decrement: (store) => {
    const { count } = store.getState();
    store.setState({ count: count - 1 });
  },

  reset: (store) => {
    store.setState({ count: 0 });
  }
};

// Utilisation
const store = new Store({ count: 0, user: null });

