const todoActions = {
  addTodo(store, text) {
    const { todos } = store.getState();
    const newTodo = {
      id: Date.now(),
      text,
      done: false
    };
    store.setState({ todos: [...todos, newTodo] });
  },

  toggleTodo(store, id) {
    const { todos } = store.getState();
    const updated = todos.map(t => 
      t.id === id ? { ...t, done: !t.done } : t
    );
    store.setState({ todos: updated });
  },

  removeTodo(store, id) {
    const { todos } = store.getState();
    store.setState({ todos: todos.filter(t => t.id !== id) });
  }
};

