// API Client avec fonctionnalit√©s avanc√©es
class ApiClient {
  constructor(baseURL, options = {}) {
    this.baseURL = baseURL;
    this.defaultHeaders = options.headers || {};
    this.timeout = options.timeout || 10000;
    this.retries = options.retries || 3;
    this.interceptors = {
      request: [],
      response: []
    };
  }

  // Ajouter un intercepteur de requ√™te
  addRequestInterceptor(callback) {
    this.interceptors.request.push(callback);
  }

  // Ajouter un intercepteur de r√©ponse
  addResponseInterceptor(callback) {
    this.interceptors.response.push(callback);
  }

  // Timeout pour les requ√™tes
  async fetchWithTimeout(url, options) {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), this.timeout);

    try {
      const response = await fetch(url, {
        ...options,
        signal: controller.signal
      });
      clearTimeout(timeoutId);
      return response;
    } catch (error) {
      clearTimeout(timeoutId);
      if (error.name === 'AbortError') {
        throw new Error('Request timeout');
      }
      throw error;
    }
  }

  // Retry logic
  async requestWithRetry(url, options, attempt = 1) {
    try {
      console.log(`üîÑ Tentative ${attempt}/${this.retries}`);
      return await this.fetchWithTimeout(url, options);
    } catch (error) {
      if (attempt < this.retries) {
        console.log(`‚è≥ Retry dans 1s...`);
        await new Promise(r => setTimeout(r, 1000));
        return this.requestWithRetry(url, options, attempt + 1);
      }
      throw error;
    }
  }

  async request(endpoint, options = {}) {
    let config = {
      headers: { ...this.defaultHeaders, ...options.headers },
      ...options
    };

    // Appliquer les intercepteurs de requ√™te
    for (const interceptor of this.interceptors.request) {
      config = await interceptor(config);
    }

    const url = `${this.baseURL}${endpoint}`;
    let response = await this.requestWithRetry(url, config);

    // Appliquer les intercepteurs de r√©ponse
    for (const interceptor of this.interceptors.response) {
      response = await interceptor(response);
    }

    if (!response.ok) {
      throw new Error(`HTTP ${response.status}`);
    }

    return response.json();
  }
}

// Utilisation
