    console.log(`üåê Cache MISS - Requ√™te r√©seau: ${cacheKey}`);

    // Simulation de fetch
    const response = await this.simulateFetch(url, options);

    // Mettre en cache (seulement GET)
    if (!options.method || options.method === 'GET') {
      this.cache.set(cacheKey, {
        data: response,
