class PaginationManager {
  constructor(apiEndpoint, pageSize = 10) {
    this.apiEndpoint = apiEndpoint;
    this.pageSize = pageSize;
    this.currentPage = 1;
    this.totalPages = null;
    this.data = [];
  }

  async fetchPage(page = this.currentPage) {
    console.log(`üìÑ Chargement page ${page}...`);

    // Simulation d'API avec pagination
    const response = await this.simulateApiCall(page);

    this.currentPage = page;
    this.totalPages = response.totalPages;
    this.data = response.items;

    console.log(`‚úÖ ${response.items.length} items charg√©s`);
    console.log(`   Page ${this.currentPage}/${this.totalPages}`);

    return response;
  }

  async nextPage() {
    if (this.hasNext()) {
      return this.fetchPage(this.currentPage + 1);
    }
    console.log('‚ö†Ô∏è Pas de page suivante');
    return null;
  }

  async prevPage() {
    if (this.hasPrev()) {
      return this.fetchPage(this.currentPage - 1);
    }
    console.log('‚ö†Ô∏è Pas de page pr√©c√©dente');
    return null;
  }

  hasNext() {
    return this.totalPages === null || this.currentPage < this.totalPages;
  }

  hasPrev() {
    return this.currentPage > 1;
  }

  async simulateApiCall(page) {
    await new Promise(r => setTimeout(r, 300));

    const totalItems = 47;
    const totalPages = Math.ceil(totalItems / this.pageSize);
    const start = (page - 1) * this.pageSize;
    const end = Math.min(start + this.pageSize, totalItems);

    const items = [];
    for (let i = start; i < end; i++) {
      items.push({
        id: i + 1,
        name: `Item ${i + 1}`,
        value: Math.random() * 100
      });
    }

    return {
      items,
      page,
      pageSize: this.pageSize,
      totalItems,
      totalPages
    };
  }
}

