// Gestionnaire WebSocket avec reconnexion
class WebSocketManager {
  constructor(url, options = {}) {
    this.url = url;
    this.reconnectDelay = options.reconnectDelay || 3000;
    this.maxReconnectAttempts = options.maxReconnectAttempts || 5;
    this.reconnectAttempts = 0;
    this.listeners = {};
    this.ws = null;
    this.shouldReconnect = true;
  }

  connect() {
    console.log(`üîå Connexion √† ${this.url}...`);

    // Simulation WebSocket
    this.ws = {
      readyState: 1, // OPEN
      send: (data) => console.log('üì§ Envoi:', data),
      close: () => console.log('üîå Connexion ferm√©e')
    };

    this.onOpen();

    // Simulation de r√©ception de messages
    setTimeout(() => {
      this.onMessage({ data: JSON.stringify({ type: 'welcome', message: 'Connect√©!' }) });
    }, 500);
  }

  onOpen() {
    console.log('‚úÖ WebSocket connect√©');
    this.reconnectAttempts = 0;
    this.emit('connected');
  }

  onMessage(event) {
    try {
      const data = JSON.parse(event.data);
      console.log('üì® Message re√ßu:', data);
      this.emit('message', data);

      // √âmettre un √©v√©nement sp√©cifique selon le type
      if (data.type) {
        this.emit(data.type, data);
      }
    } catch (error) {
      console.error('‚ùå Erreur parsing message:', error);
    }
  }

  onClose() {
    console.log('üîå WebSocket ferm√©');
    this.emit('disconnected');

    if (this.shouldReconnect && this.reconnectAttempts < this.maxReconnectAttempts) {
      this.reconnectAttempts++;
      console.log(`üîÑ Reconnexion (${this.reconnectAttempts}/${this.maxReconnectAttempts}) dans ${this.reconnectDelay}ms...`);
      setTimeout(() => this.connect(), this.reconnectDelay);
    }
  }

  send(data) {
    if (this.ws && this.ws.readyState === 1) {
      this.ws.send(JSON.stringify(data));
    } else {
      console.error('‚ùå WebSocket non connect√©');
    }
  }

  on(event, callback) {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }
    this.listeners[event].push(callback);
  }

  emit(event, data) {
    if (this.listeners[event]) {
      this.listeners[event].forEach(callback => callback(data));
    }
  }

  disconnect() {
    this.shouldReconnect = false;
    if (this.ws) {
      this.ws.close();
    }
  }
}

