class AuthService {
  constructor() {
    this.token = null;
    this.user = null;
    this.tokenKey = 'auth_token';
    this.userKey = 'auth_user';
  }

  // Connexion
  async login(email, password) {
    console.log(`üîê Tentative de connexion: ${email}`);

    try {
      // Simulation d'appel API
      const response = await this.mockLoginAPI(email, password);

      // Stocker le token et les donn√©es utilisateur
      this.setToken(response.token);
      this.setUser(response.user);

      console.log('‚úÖ Connexion r√©ussie!');
      console.log('   User:', response.user.name);
      console.log('   Token:', response.token.substring(0, 20) + '...');

      return response;

    } catch (error) {
      console.error('‚ùå √âchec de connexion:', error.message);
      throw error;
    }
  }

  // D√©connexion
  logout() {
    console.log('üëã D√©connexion...');
    this.token = null;
    this.user = null;
    // En production: localStorage.removeItem(this.tokenKey)
    console.log('‚úÖ D√©connexion r√©ussie');
  }

  // V√©rifier si authentifi√©
  isAuthenticated() {
    if (!this.token) return false;

    // V√©rifier l'expiration du token
    const payload = this.decodeToken(this.token);
    if (!payload) return false;

    const now = Date.now() / 1000;
    const isValid = payload.exp > now;

    if (!isValid) {
      console.log('‚ö†Ô∏è Token expir√©');
      this.logout();
    }

    return isValid;
  }

  // D√©coder le JWT (sans v√©rification de signature)
  decodeToken(token) {
    try {
      const parts = token.split('.');
      if (parts.length !== 3) return null;

      const payload = JSON.parse(atob(parts[1]));
      return payload;
    } catch (error) {
      return null;
    }
