class SecureLoginForm {
  constructor(authService) {
    this.authService = authService;
    this.attempts = 0;
    this.maxAttempts = 3;
    this.lockoutTime = 5000;
    this.isLocked = false;
  }

  validateInputs(email, password) {
    const errors = [];

    if (!email || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      errors.push('Email invalide');
    }

    if (!password || password.length < 8) {
      errors.push('Mot de passe trop court (min 8 caract√®res)');
    }

    return errors;
  }

  sanitize(str) {
    return str.replace(/[<>"']/g, '');
  }

  async attemptLogin(email, password) {
    if (this.isLocked) {
      console.log('üîí Compte bloqu√© temporairement');
      return;
    }

    const errors = this.validateInputs(email, password);
    if (errors.length > 0) {
      console.log('‚ùå Erreurs:', errors.join(', '));
      return;
    }

    const cleanEmail = this.sanitize(email);
    const cleanPassword = this.sanitize(password);

    try {
      await this.authService.login(cleanEmail, cleanPassword);
      this.attempts = 0;
      console.log('‚úÖ Connexion r√©ussie!');
    } catch (error) {
      this.attempts++;
      console.log(`‚ùå √âchec ${this.attempts}/${this.maxAttempts}`);

      if (this.attempts >= this.maxAttempts) {
        this.lockAccount();
      }
    }
  }

  lockAccount() {
    this.isLocked = true;
    console.log(`üîí Compte bloqu√© pour ${this.lockoutTime/1000}s`);
    setTimeout(() => {
      this.isLocked = false;
      this.attempts = 0;
      console.log('üîì Compte d√©bloqu√©');
    }, this.lockoutTime);
  }
}

