// Service d'authentification avec refresh token
class AdvancedAuthService {
  constructor() {
    this.accessToken = null;
    this.refreshToken = null;
    this.tokenExpiry = null;
    this.refreshPromise = null;
  }

  async login(email, password) {
    console.log('üîê Connexion...');
    const response = await this.mockLoginAPI(email, password);

    this.setTokens(response.accessToken, response.refreshToken);
    console.log('‚úÖ Tokens stock√©s');

    // D√©marrer le refresh automatique
    this.scheduleTokenRefresh();

    return response;
  }

  setTokens(accessToken, refreshToken) {
    this.accessToken = accessToken;
    this.refreshToken = refreshToken;

    // D√©coder et stocker l'expiration
    const payload = this.decodeToken(accessToken);
    this.tokenExpiry = payload.exp * 1000;
  }

  async refreshAccessToken() {
    // √âviter les refresh multiples simultan√©s
    if (this.refreshPromise) {
      return this.refreshPromise;
    }

    console.log('üîÑ Refresh du token...');

    this.refreshPromise = (async () => {
      try {
        const response = await this.mockRefreshAPI(this.refreshToken);
        this.setTokens(response.accessToken, this.refreshToken);
        console.log('‚úÖ Token refreshed');
        this.scheduleTokenRefresh();
        return response.accessToken;
      } finally {
        this.refreshPromise = null;
      }
    })();

    return this.refreshPromise;
  }

  scheduleTokenRefresh() {
    // Refresh 5 minutes avant expiration
    const refreshTime = this.tokenExpiry - Date.now() - (5 * 60 * 1000);

    if (refreshTime > 0) {
      console.log(`‚è∞ Refresh programm√© dans ${Math.round(refreshTime/1000)}s`);
      setTimeout(() => this.refreshAccessToken(), refreshTime);
    }
  }

  async getValidToken() {
    const now = Date.now();
    const buffer = 60 * 1000; // 1 minute de buffer

    if (this.tokenExpiry - now < buffer) {
      console.log('‚ö†Ô∏è Token expir√© ou proche expiration');
      return this.refreshAccessToken();
    }

    return this.accessToken;
  }

  decodeToken(token) {
    const parts = token.split('.');
    return JSON.parse(atob(parts[1]));
  }

  async mockLoginAPI(email, password) {
    await new Promise(r => setTimeout(r, 300));

    const now = Math.floor(Date.now() / 1000);
    const accessPayload = btoa(JSON.stringify({
      sub: '123',
      email: email,
      exp: now + 900 // 15 minutes
    }));

    return {
      accessToken: `header.${accessPayload}.signature`,
      refreshToken: 'refresh_token_xyz_123',
      expiresIn: 900
    };
  }

  async mockRefreshAPI(refreshToken) {
    await new Promise(r => setTimeout(r, 300));

    const now = Math.floor(Date.now() / 1000);
    const accessPayload = btoa(JSON.stringify({
      sub: '123',
      email: 'user@example.com',
      exp: now + 900
    }));

    return {
      accessToken: `header.${accessPayload}.signature_new`,
      expiresIn: 900
    };
  }
}

