// Op√©rateurs RxJS simul√©s
const map = (transformFn) => (observable) => {
  return new Observable((observer) => {
    return observable.subscribe({
      next: (value) => observer.next(transformFn(value)),
      error: (err) => observer.error(err),
      complete: () => observer.complete()
    });
  });
};

const filter = (predicateFn) => (observable) => {
  return new Observable((observer) => {
    return observable.subscribe({
      next: (value) => {
        if (predicateFn(value)) {
          observer.next(value);
        }
      },
      error: (err) => observer.error(err),
      complete: () => observer.complete()
    });
  });
};

const tap = (sideEffectFn) => (observable) => {
  return new Observable((observer) => {
    return observable.subscribe({
      next: (value) => {
        sideEffectFn(value);
        observer.next(value);
      },
      error: (err) => observer.error(err),
      complete: () => observer.complete()
    });
  });
};

