// Simulation Observable simple
class Observable {
  constructor(private subscribeFn) {}
  subscribe(observer) {
    return this.subscribeFn(observer);
  }
}

const of = (...values) => new Observable((observer) => {
  values.forEach(v => observer.next(v));
  observer.complete();
});

interface Todo {
  id: number;
  title: string;
  completed: boolean;
}

class TodoService {
  private todos: Todo[] = [];
  private nextId = 1;

  getAll() {
    console.log('üìã R√©cup√©ration de toutes les t√¢ches');
    return of(this.todos);
  }

  create(title: string) {
    const todo: Todo = {
      id: this.nextId++,
      title,
      completed: false
    };
    this.todos.push(todo);
    console.log(`‚úÖ T√¢che cr√©√©e: ${title}`);
    return of(todo);
  }

  toggle(id: number) {
    const todo = this.todos.find(t => t.id === id);
    if (todo) {
      todo.completed = !todo.completed;
      console.log(`üîÑ T√¢che ${id} ${todo.completed ? 'compl√©t√©e' : 'r√©ouverte'}`);
      return of(todo);
    }
    return of(null);
  }

  delete(id: number) {
    const index = this.todos.findIndex(t => t.id === id);
    if (index > -1) {
      this.todos.splice(index, 1);
      console.log(`üóëÔ∏è T√¢che ${id} supprim√©e`);
      return of(true);
    }
    return of(false);
  }
}

const service = new TodoService();
service.create('Apprendre Angular').subscribe();
service.create('Faire le TP').subscribe();
service.toggle(1).subscribe();
service.getAll().subscribe({
  next: (todos) => {
