// Simulation des FormControl et Validators
class FormControl {
  constructor(private initialValue: any, private validators = []) {
    this.value = initialValue;
    this.errors = this.validate();
  }

  value: any;
  errors: any = null;

  setValue(value: any) {
    this.value = value;
    this.errors = this.validate();
  }

  validate() {
    const errors: any = {};

    for (const validator of this.validators) {
      const error = validator(this.value);
      if (error) {
        Object.assign(errors, error);
      }
    }

    return Object.keys(errors).length > 0 ? errors : null;
  }

  get valid() {
    return this.errors === null;
  }

  get invalid() {
    return !this.valid;
  }
}

class FormGroup {
  constructor(private controls: Record<string, FormControl>) {}

  get(name: string) {
    return this.controls[name];
  }

  get value() {
    const result: any = {};
    Object.keys(this.controls).forEach(key => {
      result[key] = this.controls[key].value;
    });
    return result;
  }

  get valid() {
    return Object.values(this.controls).every(c => c.valid);
  }
}

// Validators
const Validators = {
  required: (value: any) => {
    return !value || value.trim() === '' ? { required: true } : null;
  },

  minLength: (min: number) => (value: string) => {
    return value && value.length < min ? { minLength: { min, actual: value.length } } : null;
  },

  email: (value: string) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return value && !regex.test(value) ? { email: true } : null;
  }
};

// Cr√©ation d'un formulaire
const userForm = new FormGroup({
  name: new FormControl('', [Validators.required, Validators.minLength(3)]),
  email: new FormControl('', [Validators.required, Validators.email]),
  password: new FormControl('', [Validators.required, Validators.minLength(8)])
});

console.log('=== Reactive Forms Demo ===\n');

// Test validation
console.log('√âtat initial:', userForm.valid ? 'Valide' : 'Invalide');

// Remplir le formulaire
userForm.get('name')?.setValue('Al');
console.log('\nNom = "Al"');
console.log('  Valide?', userForm.get('name')?.valid);
console.log('  Erreurs:', userForm.get('name')?.errors);

userForm.get('name')?.setValue('Alice');
console.log('\nNom = "Alice"');
console.log('  Valide?', userForm.get('name')?.valid);

userForm.get('email')?.setValue('invalid-email');
console.log('\nEmail = "invalid-email"');
console.log('  Erreurs:', userForm.get('email')?.errors);

userForm.get('email')?.setValue('alice@example.com');
userForm.get('password')?.setValue('SecurePass123');

console.log('\n‚úÖ Formulaire complet:');
console.log('  Valide?', userForm.valid);
console.log('  Valeurs:', userForm.value);

// Simulation HttpClient et Interceptors
class HttpRequest {
  constructor(public method: string, public url: string, public body?: any) {
    this.headers = {};
  }
  headers: Record<string, string>;

  clone(options: any) {
    const cloned = new HttpRequest(this.method, this.url, this.body);
    cloned.headers = { ...this.headers, ...options.headers };
    return cloned;
  }
}

class HttpResponse {
  constructor(public body: any, public status = 200) {}
}

// Interceptor pour ajouter le token
class AuthInterceptor {
  intercept(req: HttpRequest, next: any) {
    console.log(`üîê Interceptor: Ajout du token √† ${req.method} ${req.url}`);

    const token = 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9...';
    const authReq = req.clone({
      headers: { 'Authorization': token }
    });

    return next.handle(authReq);
  }
}

// Interceptor pour logger
class LoggingInterceptor {
  intercept(req: HttpRequest, next: any) {
    const started = Date.now();
    console.log(`üìä Request: ${req.method} ${req.url}`);

    return {
      subscribe: (observer: any) => {
        const result = next.handle(req);
        result.subscribe({
          next: (response: any) => {
            const elapsed = Date.now() - started;
            console.log(`‚úÖ Response: ${response.status} (${elapsed}ms)`);
            observer.next(response);
          },
          error: (err: any) => observer.error(err)
        });
      }
    };
  }
}

// HttpClient simplifi√©
class HttpClient {
  constructor(private interceptors: any[] = []) {}

  get(url: string) {
    return this.request('GET', url);
  }

  post(url: string, body: any) {
    return this.request('POST', url, body);
  }

  request(method: string, url: string, body?: any) {
    let req = new HttpRequest(method, url, body);

    // Cha√Æne des interceptors
    const chain = this.interceptors.reduceRight(
      (next, interceptor) => {
        return {
          handle: (request: HttpRequest) => interceptor.intercept(request, next)
        };
      },
      {
        handle: (request: HttpRequest) => {
          return {
            subscribe: (observer: any) => {
              setTimeout(() => {
                const response = new HttpResponse({ data: 'Mock data' });
                observer.next(response);
              }, 300);
            }
          };
        }
      }
    );

    return chain.handle(req);
  }
